import { createSlice } from "@reduxjs/toolkit";

const ListSlice= createSlice ({
    name: "Universite",
    initialState: {
        Universites: [
            {Université: "Université Mohamed V - Rabat",Site_web: "www.um5.ac.ma"},

{Université:"Université Hassan II - Casablanca",Site_web: "www.univh2c.ma"},

{Université: "Université Sidi Mohammed Ben Abdellah - Fès",Site_web: "www.usmba.ac.ma"
},
