{Université: "Université Sultan Moulay Slimane – Béni Mellal", Site_web:"www.usms.ac.ma"}
        ]
    },
    
    reducers:{
        thisit(state){test=eval(state.Universites)
        console.log(test)},
filterlist(state, search){state.todolist.filter((item)=> search==="" ? state.todolist : item.Titre.toLocaleLowerCase().includes(search.toString().toLocaleLowerCase()))}
    }
})
export const {filterlist, thisit}= ListSlice.actions
